--Top donors to Gitcoin Rounds
-- donor
-- total_amount_donated

SELECT 
    donor,
    SUM(amount_raw / 1e18) AS total_amount_donated
FROM 
    gitcoin.donations
GROUP BY 
    donor
ORDER BY 
    total_amount_donated DESC
LIMIT 30;


