-- transaction_date
-- token
-- total_transacted_volume

WITH AllTokenTransactions AS (
  SELECT
    DATE(evt_block_time) AS transaction_date,
    token,
    TRY_CAST(amount AS DECIMAL) AS amount
  FROM valora_celo.WalletJumpstart_evt_ERC20Deposited
  UNION ALL
  SELECT
    DATE(evt_block_time) AS transaction_date,
    token,
    TRY_CAST(amount AS DECIMAL) AS amount
  FROM valora_celo.WalletJumpstart_evt_ERC20Reclaimed
)
SELECT
  transaction_date,
  token,
  COALESCE(SUM(amount), 0) AS total_transacted_volume
FROM AllTokenTransactions
GROUP BY
  transaction_date,
  token
ORDER BY
  transaction_date,
  token


  
