-- block_date
-- blockchain
-- daily_amount_usd
-- daily_transaction_count

SELECT
  block_date,
  blockchain,
  SUM(amount_usd) AS daily_amount_usd,
  COUNT(*) AS daily_transaction_count
FROM
  curvefi.trades
GROUP BY
  block_date,
  blockchain
ORDER BY
  block_date,
  blockchain;



