
-- currency_symbol
-- month
-- total_amount
-- total_amount_usd
-- total_transactions
-- unique_recipients

SELECT
  currency_symbol,
  DATE_TRUNC('month', block_time) AS month,
  SUM(amount) AS total_amount,
  SUM(amount_usd) AS total_amount_usd,
  COUNT(DISTINCT tx_hash) AS total_transactions,
  COUNT(DISTINCT recipient) AS unique_recipients
FROM (
  SELECT
    *
  FROM equalizer_optimism.flashloans
  UNION ALL
  SELECT
    *
  FROM equalizer_polygon.flashloans
  UNION ALL
  SELECT
    *
  FROM equalizer_ethereum.flashloans
  UNION ALL
  SELECT
    *
  FROM equalizer_bnb.flashloans
  UNION ALL
  SELECT
    *
  FROM equalizer.flashloans
) AS combined_flashloans
GROUP BY
  currency_symbol,
  DATE_TRUNC('month', block_time)
ORDER BY
  currency_symbol,
  month


