-- find number of daily active addresses
--day
--total
SELECT
  date_trunc('day', block_time) AS day,
  COUNT(DISTINCT "from") AS total
FROM gnosis.transactions
WHERE block_time > now() - INTERVAL '360 day'
GROUP BY 1
ORDER BY 1 DESC;



