-- analyse daily transaction on the gnosi chain for the past 90 days
-- day
-- total
SELECT
  DATE_TRUNC('day', block_time) AS day,
  COUNT(hash) AS total
FROM
  gnosis.transactions
WHERE
  block_time > CURRENT_TIMESTAMP - INTERVAL '90' day
GROUP BY
  1
ORDER BY
  1 DESC



