
-- blockchain
-- date
-- daily_trading_volume
-- daily_fee

SELECT
  blockchain,
  time AS date,
  SUM(trading_volume) AS daily_trading_volume,
  SUM(fee) AS daily_fee
FROM
  lido.liquidity
GROUP BY
  blockchain,
  time
ORDER BY
  blockchain,
  time;

